sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
    var checkHasRole = new GlideAjax('CheckHasRole');
    checkHasRole.addParam('sysparm_name','cRole');
	checkHasRole.addParam('sysparm_callerID',g_form.getValue('caller_id'));
	checkHasRole.getXML(hasNotRole);

	function hasNotRole(response){
		var hasRoleFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		alert(hasRoleFromScriptInclude);
		g_form.clearValue('caller_id');
		g_form.setValue('caller_id',oldValue);
	}
}
