sys_script_include.script:
var CheckHasRole = Class.create();
CheckHasRole.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	cRole :function(){
		var caller = new GlideRecord("sys_user_has_role");
		caller.addQuery('user',this.getParameter('sysparm_callerID'));
// 		caller.addQuery('role','c901c07407219c103129f87e7c1ed0e3');
		caller.query();
		while(caller.next()){
			if(caller.role == 'c901c07407219c103129f87e7c1ed0e3'){
				return "このユーザーはロールを持っています";
			}
		}
		return "このユーザーはロールを持っていません";
	},
    type: 'CheckHasRole'
});
